let click_elems=[document.getElementById("header-activity"),document.getElementById("header-map"),document.getElementById("header-time")],content_items=[document.getElementById("activity-content"),document.getElementById("map-content"),document.getElementById("time-content")];click_elems.forEach((e=>{e.addEventListener("click",(()=>{let t=0,c=0;click_elems.forEach((m=>{m.className="item",e===m&&(c=t),t+=1})),e.className="active-item",content_items.forEach((e=>{e.className="inactive"})),content_items[c].className="active"}))}));